import React, { Component } from "react";
import Jumbotron from "../../components/Jumbotron";
import DeleteBtn from "../../components/DeleteBtn";
import CheckedBtn from "../../components/CheckedBtn";
import API from "../../utils/API";
import { Col, Row, Container } from "../../components/Grid";
import { List, ListItem } from "../../components/List";
import { Input, FormBtn } from "../../components/Form";
// import { clone } from "@babel/types";

class Todos extends Component {
  // Setting our component's initial state
  state = {
    todos: [],
    id: "",
    done: "",
    ddescription: ""
  };

  // When the component mounts, load all todos and save them to this.state.todos
  componentDidMount() {
    this.loadTodos();
  }

  // Loads all todos  and sets them to this.state.todos
  loadTodos = () => {
    API.getTodos()
      .then(res =>
        this.setState({ todos: res.data, id: "", done: "", description: "" })
      )
      .catch(err => console.log(err));
  };

  // Marks as checked 
  handleClickTodo = id => {
    var todos = this.state.todos

    for (let i = 0; i < todos.length; i++) {
        if (todos[i].done === true) {
          console.log(i, "index of clicked");
          console.log("done:", "this.state.done");
            this.setState({
              done: true,
            });
          } else {
            this.setState({
              done: false,
            });
          }
          // for (let j = 0; j < clone.length; j++) {
          //   clone[j].beenClicked = false;
          // }
          // this.setState({
          //   score: 0,
          //   pictures: clone
          // });
        // } else {
        //   this.setState({
        //     score: this.state.score + 1,
        //     pictures: clone,
        //     message: "You clicked correctly. Keep clicking!"
        //   });
          // if (this.state.score === 11) {
          //   this.setState({
          //     bestScore: 12,
          //     pictures: clone,
          //     message:"Wow! You are a master at memory games!"
          //   });
            // for (let j = 0; j < clone.length; j++) {
            //   clone[j].beenClicked = false;
            // }
            // this.setState({
            //   score: 0,
            //   pictures: clone,
            // });
          // }
          // clone[i].beenClicked = true;
        }
        // break;
      // } 
    // }
    // clone = clone.sort(() => Math.random() - 0.5);


    // API.updateTodo(id)
    // .then(res => this.loadTodos())
    // .catch(err => console.log(err));

    // var todos = [];

    // for (let i = 0; i < todos.length; i++) {
    //   if (todos[i].done === false) {
    //     console.log("ive been clicked");
    //   }
    // }
  };

  // Deletes a todo from the database with a given id, then reloads todos from the db
  deleteTodo = id => {
    API.deleteTodo(id)
      .then(res => this.loadTodos())
      .catch(err => console.log(err));
  };

  // Handles updating component state when the user types into the input field
  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  // When the form is submitted, use the API.saveTodo method to save the todo data
  // Then reload todos from the database
  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.description ) {
      API.saveTodo({
        // id: this.state.id,
        done: this.state.done,
        description: this.state.description
      })
        .then(res => this.loadTodos())
        .catch(err => console.log(err));
    }
  };

  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-6">
            <Jumbotron>
              <h1>Add to your To Do List?</h1>
            </Jumbotron>
            <form>
              {/* <Input
                value={this.state.id || ""}
                onChange={this.handleInputChange}
                name="id"
                placeholder="id"
              /> */}
              Enter Description 
              <Input
                value={this.state.description || ""}
                onChange={this.handleInputChange}
                name="description"
                placeholder="To Do Item (Required)"
              />
              Done
              <Input
                type="text"
                value={this.state.done || ""}
                onChange={this.handleInputChange}
                name="done"
                placeholder="Enter True or False?"
              />
              <FormBtn
                disabled={!(this.state.description)}
                onClick={this.handleFormSubmit}
              >
                Submit To Do
              </FormBtn>
            </form>
          </Col>
          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>My To Do List</h1>
            </Jumbotron>
            {this.state.todos.length ? (
              <List>
                {this.state.todos.map(todo => {
                  return (
                    <ListItem key={todo._id}>
                    {this.state.todos.map(todo => (
                      <CheckedBtn
                        key={todo._id}
                        id={todo._id}
                        name={todo.done}
                        handleClickTodo={this.handleClickTodo}
                      />
                    ))}
                    {/* <CheckedBtn onClick={() => this.checkedTodo(todo._id)} /> */}
                      <a href={"/todos/" + todo._id}>
                        <strong>
                          , {todo.description}
                        </strong>
                      </a>
                      <DeleteBtn onClick={() => this.deleteTodo(todo._id)} />
                    </ListItem>
                  );
                })}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Todos;
